mapper.select:
select  * from df_uni_device
where sub_code =#{subCode,jdbcType=VARCHAR}
and bay_id =#{bayId,jdbcType=VARCHAR}
and device_name =#{deviceName,jdbcType=VARCHAR}

select  * from df_uni_device
where sub_code =#{subCode,jdbcType=VARCHAR}
and device_id =#{deviceId,jdbcType=VARCHAR}

select  * from df_uni_device
where sub_code =#{subCode,jdbcType=VARCHAR}
and bay_id =#{bayId,jdbcType=VARCHAR}
order by
convert(device_name using gbk) collate gbk_chinese_ci
