mapper.select:
select dups.*, dus.sub_name from df_uni_patrol_system dups left join df_pub_substation dus on dups.sub_code = dus.sub_code
and dups.sys_name like concat('%', #{sysName,jdbcType=VARCHAR}, '%')
and dups.sys_type =#{sysType,jdbcType=VARCHAR}
order by dups.id limit #{start},#{size}

select count(1) from df_uni_patrol_system dups


        and dups.sys_name like concat('%', #{sysName,jdbcType=VARCHAR}, '%')


        and dups.sys_type =#{sysType,jdbcType=VARCHAR}
