mapper.select:
select  * from df_uni_point_excel_import_error


        and error_file_id = #{excelId,jdbcType=INTEGER}


        and error_reason like concat('%',#{errorReason,jdbcType=VARCHAR},'%')



    limit #{start,jdbcType=INTEGER},#{size,jdbcType=INTEGER}

select  count(*) from df_uni_point_excel_import_error


        and error_file_id = #{excelId,jdbcType=INTEGER}


        and error_reason like concat('%',#{errorReason,jdbcType=VARCHAR},'%')
